import { Box, Typography } from "@mui/material";
import StarBorderIcon from "@mui/icons-material/StarBorder";
import "./Item.css";
import React from "react";

const Item = ({ product }) => {
  return (
    <Box sx={{ display: "flex", flexDirection: "column",mb:"8px" }}>
      <img
        src={
          product.type === "Brand" ? product.brand_image_url : product.image_url
        }
        style={{ width: "21.5vw", height: "16vw", borderRadius: "16px" }}
      />
      <Box sx={{ display: "flex", justifyContent: "space-between", mt: "8px" }}>
        <Typography className="type_all">
          {product.type === "Brand"
            ? product.brand_name
            : product.type === "Category"
            ? "#" + product.title
            : product.title}
        </Typography>
        <Typography
          className={
            product.type === "Product" ? "type_all type_Product " : "type_all"
          }
        >
          {product.type === "Brand"
            ? "관심고객수"
            : product.type === "Product"
            ? product.discountPercentage + "%"
            : ""}
        </Typography>
      </Box>
      <Box sx={{ display: "flex", justifyContent: "space-between" }}>
        <Typography>
          {product.type === "Exhibition" && product.sub_title}
        </Typography>
        <Typography>
          {product.type === "Brand"
            ? product.follower
            : product.type === "Product" && product.price + "원"}
        </Typography>
      </Box>
    </Box>
  );
};

export default Item;
