import React, { useEffect, useRef, useState } from "react";
import { itemData } from "./Recoil_Data";
import { useRecoilValue } from "recoil";
import { Box } from "@mui/material";
import Item from "./Item";
import "./ProductList.css";

const ProductList = () => {
  const data = useRecoilValue(itemData);
  const [displayCount, setDisplayCount] = useState(20); // 표시할 아이템 항목 수
  const incrementAmount = 20; // 무한 스크롤에서 증가할 항목의 증가량
  const lastItemRef = useRef(null); // 마지막 항목을 나타내는 useRef
  const itemList = data.slice(0, displayCount).map((e, idx) => {
    if (idx === data.length - 1) {
      return (
        <Item key={`item_ProductList${idx}`} product={e} ref={lastItemRef} />
      );
    }
    return <Item key={`item_ProductList${idx}`} product={e} />;
  });
  const handleObserver = (entries) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting && !entry.target.classList.contains("loading")) {
        entry.target.classList.add("loading");
        setTimeout(() => {
          setDisplayCount((prevCount) => prevCount + incrementAmount);
          entry.target.classList.remove("loading");
        }, 1000);
      }
    });
  };
  useEffect(() => {
    const options = {
      root: null,
      rootMargin: "0px",
      threshold: 0.5,
    };
    const observer = new IntersectionObserver(handleObserver, options);
    if (lastItemRef.current) {
      observer.observe(lastItemRef.current);
    }
    return () => {
      if (lastItemRef.current) {
        observer.unobserve(lastItemRef.current);
      }
    };
  }, [lastItemRef]);
  return (
    <Box className="productList">
      <Box>카테고리</Box>
      <Box className="itemList">{itemList}</Box>
    </Box>
  );
};

export default ProductList;
