import React, { useEffect, useRef, useState } from "react";
import { itemData } from "./Recoil_Data";
import { useRecoilValue } from "recoil";
import { Box } from "@mui/material";
import Item from "./Item";
import "./ProductList.css";

const ProductList = () => {
  const data = useRecoilValue(itemData);
  const [page, setPage] = useState(1);
  const lastItemRef = useRef();
 const itemsPerPage = 20;
const startIndex = (page - 1) * itemsPerPage;
const endIndex = page * itemsPerPage;

const itemList = data.slice(startIndex, endIndex).map((e, idx) => {
  if (idx === endIndex - 1) {
    return <Item key={`item_ProductList${idx}`} product={e} ref={lastItemRef} />;
  } else {
    return <Item key={`item_ProductList${idx}`} product={e} />;
  }
});
  useEffect(() => {
    const options = {
      rootMargin: "0px",
      threshold: 1.0
    };

    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting) {
        setPage(page => page + 1);
      }
    }, options);

    if (lastItemRef.current) {
      observer.observe(lastItemRef.current);
    }

    // Cleanup function to disconnect the observer when the component unmounts
    return () => {
      if (lastItemRef.current) {
        observer.unobserve(lastItemRef.current);
      }
    };
  }, [lastItemRef]);
  return (
    <Box className="productList">
      <Box>카테고리</Box>
      <Box className="itemList">{itemList}</Box>
    </Box>
  );
};

export default ProductList;
