import React, { useEffect } from "react";
import "./MainPage.css";
import { Box, Typography } from "@mui/material";
import Item from "./Item";
import { itemData, todoListState } from "./Recoil_Data";
import { useRecoilValue, useSetRecoilState } from "recoil";

const MainPage = () => {
  const itemList = useRecoilValue(itemData);
  console.log(itemList)
  const setItemList = useSetRecoilState(itemData);
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(
          "http://cozshopping.codestates-seb.link/api/v1/products?count=4"
        );
        const data = await response.json();
        setItemList(data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProducts();
  }, []);

  return (
    <Box className="mainPage">
      <Box sx={{ display: "flex", flexDirection: "column", width: "100%" }}>
        <Typography variant="h5" sx={{ fontWeight: "bold", mb: "16px" }}>
          상품 리스트
        </Typography>
        <Item />
      </Box>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          width: "100%",
          mt: "24px",
        }}
      >
        <Typography variant="h5" sx={{ fontWeight: "bold", mb: "16px" }}>
          북마크 리스트
        </Typography>
        <Item />
      </Box>
    </Box>
  );
};

export default MainPage;
