import React, { useEffect, useRef } from "react";
import { itemData } from "./Recoil_Data";
import { useRecoilValue } from "recoil";
import { Box } from "@mui/material";
import Item from "./Item";
import "./ProductList.css";

const ProductList = () => {
  const data = useRecoilValue(itemData);
  const itemList = data.map((e, idx) => {
    return <Item key={`item_ProductList${idx}`} product={e} />;
  });
  const target = useRef(null);
  console.log(target)

  useEffect(() => {
    observer.observe(target.current);
  }, []);

  const option = {
    root: null,
    rootMargin: "0px 0px 0px 0px",
    thredhold: 0,
}

  const callback = () => {
    target.current.innerText += "관측되었습니다";
  };

  const observer = new IntersectionObserver(callback, options);
  return (
    <Box className="productList">
      <Box>카테고리</Box>
      <Box className="itemList">{itemList}</Box>
    </Box>
  );
};

export default ProductList;
