import React, { useEffect, useRef, useState } from "react";
import { itemData } from "./Recoil_Data";
import { useRecoilValue } from "recoil";
import { Box } from "@mui/material";
import Item from "./Item";
import "./ProductList.css";
import Category from "./Category";

const ProductList = () => {
  const data = useRecoilValue(itemData);
  const [category, setCategory] = useState("전체");
  const [itemList, setItemList] = useState([]);
  useEffect(() => {
    if (category === "전체") {
      setItemList(
        data.map((e, idx) => {
          return <Item key={`item_ProductList${idx}`} product={e} />;
        })
      );
    } else if (category === "상품") {
        setItemList(
            data.filter((el)=>el.type === "Product").map((e, idx) => {
              return <Item key={`item_ProductList${idx}`} product={e} />;
            })
          );
    } else if (category === "카테고리") {
        setItemList(
            data.filter((el)=>el.type === "Category").map((e, idx) => {
              return <Item key={`item_ProductList${idx}`} product={e} />;
            })
          );
    } else if (category === "기획전") {
        setItemList(
            data.filter((el)=>el.type === "Exhibition").map((e, idx) => {
              return <Item key={`item_ProductList${idx}`} product={e} />;
            })
          );
    } else if (category === "브랜드") {
        setItemList(
            data.filter((el)=>el.type === "Brand").map((e, idx) => {
              return <Item key={`item_ProductList${idx}`} product={e} />;
            })
          );
    }
  }, [category]);

  return (
    <Box className="productList">
      <Category category={category} setCategory={setCategory} />
      <Box className="itemList">{itemList}</Box>
    </Box>
  );
};

export default ProductList;
